function createQuery(c){var f=c.elements;var e=new Array();e.push("tracking=ignore");for(var b=0;b<f.length;b++){var a=f[b];if(a.name&&a.value){var d="";if(a.type=="select-one"){d=a[a.selectedIndex].value}else{if(a.type=="radio"||a.type=="checkbox"){if(a.checked){d=a.value}}else{d=a.value}}if(d.length){e.push(a.name+"="+encodeURIComponent(d))}}}return e.join("&")};